<script>
// Get the modal
var Mmodal = document.getElementById("MntModal");
var Fmodal = document.getElementById("ForModal");
var Wmodal = document.getElementById("WtrModal");
var Cmodal = document.getElementById("CredModal");

// Get the button that opens the modal
var Mbtn = document.getElementById("Mbtn");
var Fbtn = document.getElementById("Fbtn");
var Wbtn = document.getElementById("Wbtn");
var Cbtn = document.getElementById("Cbtn");

// Get the <span> element that closes the modal
var Mspan = document.getElementsByClassName("Mclose")[0];
var Fspan = document.getElementsByClassName("Fclose")[0];
var Wspan = document.getElementsByClassName("Wclose")[0];
var Cspan = document.getElementsByClassName("Cclose")[0];

// When the user clicks the button, open the modal 
Mbtn.onclick = function() {
  Mmodal.style.display = "block";
}
Fbtn.onclick = function() {
  Fmodal.style.display = "block";
}
Wbtn.onclick = function() {
  Wmodal.style.display = "block";
}
Cbtn.onclick = function() {
  Cmodal.style.display = "block";
}

// When the user clicks on <span> (x), close the modal
Mspan.onclick = function() {
  Mmodal.style.display = "none";
}
Fspan.onclick = function() {
  Fmodal.style.display = "none";
}
Wspan.onclick = function() {
  Wmodal.style.display = "none";
}
Cspan.onclick = function() {
  Cmodal.style.display = "none";
}

// When the user clicks anywhere outside of the modal, close it
window.onclick = function(event) {
  if (event.target == Mmodal) {
    Mmodal.style.display = "none";
  }
  if (event.target == Fmodal) {
    Fmodal.style.display = "none";
  }
  if (event.target == Wmodal) {
    Wmodal.style.display = "none";
  }
  if (event.target == Cmodal) {
    Cmodal.style.display = "none";
  }
}
//toggle switch
const toggleSwitch = document.querySelector('.theme-switch input[type="checkbox"]');
const currentTheme = localStorage.getItem('theme');

if (currentTheme) {
    document.documentElement.setAttribute('data-theme', currentTheme);
  
    if (currentTheme === 'dark') {
        toggleSwitch.checked = true;
    }
}

function switchTheme(e) {
    if (e.target.checked) {
        document.documentElement.setAttribute('data-theme', 'dark');
        localStorage.setItem('theme', 'dark');
    }
    else {        document.documentElement.setAttribute('data-theme', 'light');
          localStorage.setItem('theme', 'light');
    }    
}

toggleSwitch.addEventListener('change', switchTheme, false);
</script>
